import React from 'react'
import Projects from './Projects';
import axios from 'axios';

const API_KEY = "1e90d005747d5d491d09b969b2b73da1";
const URL = "api.openweathermap.org/data/2.5/weather?q=Manchester,uk&appid="
class About extends React.Component {
    state = {
        temp: null,
        weather: '',
        humidity: null
    }

    async componentDidMount(){
        console.log('test');
        const api_call = await axios.get(`http://api.openweathermap.org/data/2.5/weather?lat=19&lon=70&units=metric&APPID=${API_KEY}`);
        console.log(api_call);
        this.setState({temp: api_call.data.main.temp,
                       humidity: api_call.data.main.humidity,
                       weather:  api_call.data.weather[0].description})

        console.log(api_call);
        /* const data = api_call().then(function(res){
            console.log(res);
        }); */

    }




    render(){
        return(
            <div className = "container" id="About">
                    <Projects />
                    <hr/>

                    <div className="card-row" style={{paddingBottom: 200}}>
                        <div className="ms-card col-s-4 col-m-6">
                            <div className="ms-title ">
                                <h5>Current Weather</h5>
                            </div>
                            <div className="ms-text ms-text-gray-font">
                                <p className="center"> {this.state.temp} degrees celcuis</p>
                                <p className="text-center"> {this.state.humidity} humidity </p>
                                <p className="center"> with {this.state.weather}  </p>
                            </div>
                        </div>

                        <div className="ms-card col-s-4 col-m-6">
                            <div className="ms-title ">
                                <h5>Quasi, mollitia!</h5>
                            </div>
                            <div className="ms-text ms-text-gray-font">
                                <p>
                                    Lorem ipsum dolor sit amet...
                                </p>
                            </div>
                        </div>    
                    </div>
            </div>
        )
    }

}

export default About
